import { isDisabled } from "@testing-library/user-event/dist/utils";
import React, { useState } from "react";
import "./App.css";
import Square from "./components/Square";

// const drawGame = squares.every(value => value !== null)

const App = () => {
  //////////---------- SET STATES ----------//////////
  // Empty array that will contain our <Square /> component.
  const [squares, setSquares] = useState(Array(9).fill(null));
  const [player, setPlayer] = useState("☀️");

  const [playerStatus, setPlayerStatus] = useState("Click a square to start.");
  const [winner, setWinner] = useState(null);
  // Alert Messages (for win/tie)
  // const [message, setMessage] = useState("");

  //////////---------- VARIABLES ----------//////////

  //////////---------- FUNCTIONS ----------//////////

  //////////---------- DISABLE BOARD ----------//////////
  const stopPlay = () => {
    const board = document.querySelector(".game_container");
    board.addEventListener("click", (event) => {
      event.preventDefault();
    });
  };

  ////////---------- RESET BOARD ----------//////////
  const handleReset = () => {
    setSquares(Array(9).fill(null));
    setPlayerStatus("Click a square to start.");
    setPlayer("☀️");
  };

  //////////---------- WIN / TIE CHECK ----------//////////
  const checkWin = () => {
    /// All possible win conditions, stored inside arrays
    let wins = [
      [0, 1, 2],
      [3, 4, 5],
      [6, 7, 8],
      [0, 3, 6],
      [1, 4, 7],
      [2, 5, 8],
      [0, 4, 8],
      [2, 4, 6],
    ];

    wins.forEach((v) => {
      const [a, b, c] = v;

      if (squares[a] === "☀️" && squares[b] === "☀️" && squares[c] === "☀️") {
        // stopPlay();
        // setPlayerStatus("Player 1 wins!");
        console.log("Player 1 wins!");
        // setWinner("☀️");
        // stopPlay();
      } else if (
        squares[a] === "🌧" &&
        squares[b] === "🌧" &&
        squares[c] === "🌧"
      ) {
        // stopPlay();
        // setPlayerStatus("Player 2 wins!");
        // setWinner("🌧");
        // stopPlay();
        console.log("Player 2 wins!");
      }
    });

    // for (let i = 0; i < wins.length; i++) {
    //   const [a, b, c] = wins[i];
    //   if (squares[a] === "☀️" && squares[b] === "☀️" && squares[c] === "☀️") {
    //     // stopPlay();
    //     // setPlayerStatus("Player 1 wins!");
    //     setWinner("☀️");
    //     stopPlay();
    //     console.log("Player 1 wins!");
    //   } else if (
    //     squares[a] === "🌧" &&
    //     squares[b] === "🌧" &&
    //     squares[c] === "🌧"
    //   ) {
    //     // stopPlay();
    //     // setPlayerStatus("Player 2 wins!");
    //     setWinner("🌧");
    //     stopPlay();
    //     console.log("Player 2 wins!");
    //   } else if (squares.indexOf(null) === -1) {
    //     // stopPlay();
    //     //// This is currently logging 8 times to the console, maybe one for each square index?
    //     // setPlayerStatus("It's a tie!");
    //     console.log("It's a tie!");
    //   }
    // }
  };

  //////////---------- ONCLICK GAMEPLAY ----------//////////
  const handleGamePlay = (index) => {
    console.log("clicked", index);
    let updateBoard = [...squares];

    let wins = [
      [0, 1, 2],
      [3, 4, 5],
      [6, 7, 8],
      [0, 3, 6],
      [1, 4, 7],
      [2, 5, 8],
      [0, 4, 8],
      [2, 4, 6],
    ];

    wins.forEach((v) => {
      const [a, b, c] = v;

      if (squares[a] === "☀️" && squares[b] === "☀️" && squares[c] === "☀️") {
        // stopPlay();
        // setPlayerStatus("Player 1 wins!");
        console.log("Player 1 wins!");
        // setWinner("☀️");
        // stopPlay();
      } else if (
        squares[a] === "🌧" &&
        squares[b] === "🌧" &&
        squares[c] === "🌧"
      ) {
        // stopPlay();
        // setPlayerStatus("Player 2 wins!");
        // setWinner("🌧");
        // stopPlay();
        console.log("Player 2 wins!");
      }
    });

    if (squares[index] === null && !winner) {
      updateBoard[index] = player;
      setSquares(updateBoard);
      setPlayer(player === "☀️" ? "🌧" : "☀️");
      setPlayerStatus(
        player === "☀️" ? "P2, it's your turn." : "P1, it's your turn."
      );
    }
    checkWin();
  };

  return (
    <>
      <div className="main_container">
        <h1 className="primary_header">Tic-Tac-Toe</h1>
        <div className="player_status">{playerStatus}</div>
        <div className="bg_wrap">
          <div className="game_container">
            {squares.map((squares, index) => {
              return (
                <Square
                  squares={squares}
                  handleGamePlay={handleGamePlay}
                  index={index}
                  key={index}
                />
              );
            })}
          </div>
        </div>
        <a href="#" className="btn" onClick={handleReset}>
          Start Over?
        </a>
        <footer>
          <p>&copy; Ariana Brashear, 2022</p>
        </footer>
      </div>
    </>
  );
};

export default App;
