import React, { useState } from "react";
import "./App.css";
import Square from "./components/Square";

const App = () => {
  //////////---------- SET STATES ----------//////////
  // Empty array that will contain our <Square /> component.
  const [squares, setSquares] = useState(Array(9).fill(null));
  // Players, set to start with player 1.
  const [player1, setPlayer1] = useState(true);
  const [player2, setPlayer2] = useState(false);
  // Alert Messages (for win/tie)
  const [message, setMessage] = useState("");

  //////////---------- VARIABLES ----------//////////

  /// All possible win conditions, stored inside arrays
  let wins = [
    [0, 1, 2],
    [3, 4, 5],
    [6, 7, 8],
    [0, 3, 6],
    [1, 4, 7],
    [2, 5, 8],
    [0, 4, 8],
    [2, 4, 6],
  ];

  //////////---------- FUNCTIONS ----------//////////
  const handleReset = () => {
    setSquares(Array(9).fill(null));
    setPlayer1(true);
    setPlayer2(false);
  };

  for (let i = 0; i < wins.length; i++) {
    const [a, b, c] = wins[i];
    if (squares[a] && squares[a] === squares[b] && squares[a] === squares[c]) {
      setMessage(<h1>You Win!</h1>);
    }
  }
  //////////---------- ONCLICK GAMEPLAY ----------//////////
  const handleGamePlay = (index) => {
    let updateBoard = [...squares];

    if (
      player1 === true &&
      updateBoard[index] !== "ðŸŽƒ" &&
      updateBoard[index] !== "ðŸŽ„"
    ) {
      updateBoard[index] = "ðŸŽƒ";
      setSquares(updateBoard);
      setPlayer2(true);
      setPlayer1(false);
    } else if (
      player2 === true &&
      updateBoard[index] !== "ðŸŽƒ" &&
      updateBoard[index] !== "ðŸŽ„"
    ) {
      updateBoard[index] = "ðŸŽ„";
      setSquares(updateBoard);
      setPlayer2(false);
      setPlayer1(true);
    }
  };

  return (
    <>
      <h1>Tic Tac Toe</h1>

      <div className="game_container">
        {message}
        {squares.map((squares, index) => {
          return (
            <Square
              squares={squares}
              handleGamePlay={handleGamePlay}
              index={index}
              key={index}
            />
          );
        })}
      </div>
    </>
  );
};

export default App;
